import React from 'react'
// import { useParams } from 'react-router-dom/cjs/react-router-dom.min'
import { useParams } from 'react-router-dom';

const QuestionsDetails = () => {

    // consider using useParams as object
    // const { id } = useParams();
    // console.log(id)
    const id = 2 ;

    var questionsList = [{  
        _id: 1, 
        upVotes: 3, 
        downVotes: 2,
        no0fAnswers: 2, 
        questionTitle: "What is a function-1?", 
        questionBody: "It meant to be", 
        questionTags: ["java","node js","react js"], 
        userPosted: "Sinch",
        userId:1, 
        askedOn: "jan 1" ,
        answer: [{
          answerBody: "Answer",
          userAnswered: "kumar",
          answeredOn: "jan 2",
          userId: 2
        }]
      },{
        _id: 2, 
        upVotes: 3, 
        downVotes: 2,
        no0fAnswers: 2, 
        questionTitle: "What is a function-2?", 
        questionBody: "It meant to be", 
        questionTags: ["java","node js","react js"], 
        userPosted: "Sinch",
        userId:1, 
        askedOn: "jan 1" ,
        answer: [{
          answerBody: "Answer",
          userAnswered: "kumar",
          answeredOn: "jan 2",
          userId: 2
        }]
      },{
        _id: 3, 
        upVotes: 3, 
        downVotes: 2,
        no0fAnswers: 2, 
        questionTitle: "What is a function-3?", 
        questionBody: "It meant to be", 
        questionTags: ["java","node js","react js"], 
        userPosted: "Sinch",
        userId:1, 
        askedOn: "jan 1" ,
        answer: [{
          answerBody: "Answer",
          userAnswered: "kumar",
          answeredOn: "jan 2",
          userId: 2
        }]
      }]

      // const res = questionsList.filter(question => question._id === id)
      // console.log(res)
      // res.map(question => (
      //   console.log(question.questionTitle)
      // ))
  return (
    <div className='question-details-page'>
        {
            questionsList === null ?
            <h1>Loading...</h1> :
            <>
                {
                  questionsList
                  .filter((question) => question._id === id)
                  .map((question) => (
                    <div key={question._id}>
                      <section className="question-details-container">
                        <h1>{question.questionTitle}</h1>
                        <div className="question-details-container-2">
                          <div className="question-votes">
                                   
                          </div>
                              </div>
                        </section>
                    </div>
                  ))
                }
            </>
        }
    </div>
  )
}

export default QuestionsDetails



