import React from 'react'

const Avatar = ({ children, backgroundColor, px, py, color, borderRadius, fontSize, cursor }) => {
  const style = {
      backgroundColor, 
      padding: `${py} ${px}`,  /* py: top & bottom    px : left & right padding  */
      color: color || 'black', 
      borderRadius, 
      fontSize, 
      textAlign : 'center',
      cursor: cursor || null,
      marginRight : '20px'
    }

  return (
    <div style={style}>
        { children }
    </div>
  )
}

export default Avatar

