import React, { useState } from 'react'
import './Auth.css'
import icon from '../../assets/logo3.png'

const Auth = () => {

  const [isSignup, setIsSignup] = useState(false)

  return (
    <section className="auth-section">
        <div className="auth-container">
          {!isSignup && <img src={icon} alt="stack-overflow" className="login-logo" width="250px"/>}
          <form action="">
            <label htmlFor="email">
              <h4>Email</h4>
              <input type="email" name='email' id='email'/>
            </label>
            <label htmlFor="password">
              <div>
                <h4>Password</h4>
                <h4>forgot password?</h4>
              </div>
              <input type="password" name='password' id='password'/>
            </label>
            <button type='button' className='auth-btn'>{ isSignup ? 'Sign up' : 'Log in'}</button>
          </form>
          <p>
            { !isSignup ? "Don't have an account?" : 'already have an account ?'}
            <button type='button' className='handle-switch-btn'>{ !isSignup ? 'Sign up' : 'Log in'}</button>
          </p>
        </div>
    </section>
  )
}

export default Auth


/* !isSignup === login  */

// 'htmlFor'  &  'id' of input tag  -->  must be same

