import React, { useState } from 'react'
import './Auth.css'
import icon from '../../assets/logo3.png'

const Auth = () => {

  const [isSignup, setIsSignup] = useState(false)

  const handleSwitch = () => {
    setIsSignup(!isSignup)
  }

  return (
    <section className="auth-section">
        <div className="auth-container">
          {/* {!isSignup && */<img src={icon} alt="stack-overflow" className="login-logo" width="250px"/>}
          <form>
            {
              isSignup && (
                <label htmlFor='name'>
                  <h4>Display Name</h4>
                  <input type="text" name='name' id='name'/>
                </label>
              )
            }
            <label htmlFor="email">
              <h4>Email</h4>
              <input type="email" name='email' id='email'/>
            </label>
            <label htmlFor="password">
              <div>
                <h4>Password</h4>
                { !isSignup && <h4>forgot password?</h4> }
              </div>
              <input type="password" name='password' id='password'/>
              { isSignup && <p>Password must consist atleast eight<br />characters, including atleast 1 letter and 1<br />number</p> }
            </label>
            <input type="checkbox"/>
            <p>Opt-in to receive occasional product updates, user research invitations, company announcements, and digests.</p>
            <button type='button' className='auth-btn'>{ isSignup ? 'Sign up' : 'Log in'}</button>
          </form>
          <p>
            { !isSignup ? "Don't have an account?" : 'Already have an account?'}
            <button type='button' className='handle-switch-btn' onClick={handleSwitch}>{ !isSignup ? 'Sign up' : 'Log in'}</button>
          </p>
        </div>
    </section>
  )
}

export default Auth


/* !isSignup === login  */

// 'htmlFor'  &  'id' of input tag  -->  must be same

